mapper.update:
UPDATE `member`
SET authKey = #{authKey} 
WHERE delStatus = 0 AND loginId = #{loginId}

UPDATE `member` 
SET authStatus = 1 
WHERE delStatus = 0 AND loginId = #{loginId}

mapper.select:
SELECT *
FROM `member`
WHERE delStatus = 0 AND authStatus = 1 AND loginId = #{loginId}

mapper.update:
UPDATE `member` 
SET delStatus = 1 
WHERE delStatus = 0 AND memberId = #{memberId}

UPDATE `member` 
SET loginPw = #{ecryptPw}
WHERE delStatus = 0 AND loginId = #{loginId}
